catalog_ui_policy.script_true:
function onCondition() {
    var step = g_form.getValue('current_step');
    console.log("onCondition");
    var allSteps = ['step_1', 'step_2', 'step_3', 'step_4', 'step_5'];
    allSteps.forEach(function(container) {
        g_form.setDisplay(container, false);
    });

    var target = 'step_' + step;
    g_form.setDisplay(target, true);
}
