sp_widget.client_script:
function StepNavigatorWidgetController($scope) {
  $scope.currentStep = 1;

  $scope.setStep = function(step) {
    $scope.currentStep = step;

    if (typeof g_form !== 'undefined') {
      g_form.setValue("current_step", step);
    } else {
      console.warn('g_form is not available in this context');
    }
  };
}

