sys_script_include.script:
var GetStepHtml = Class.create();
GetStepHtml.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getStepHtml: function() {

                        var html = `
<div id="stepNav" style="display: flex; font-family: Arial; font-size: 14px; border: 1px solid #ccc; border-radius: 6px; overflow: hidden;">
  ${[1, 2, 3, 4, 5].map(function (n) {
    return `
      <div class="step-button" data-step="${n}" id="step-${n}" 
           style="flex: 1; padding: 10px; text-align: center; cursor: pointer; border-right: 1px solid #ccc; background: white;">
        Step${n}
      </div>`;
  }).join('')}
</div>
        `;
        return html;
    },
});
